    public partial class Entity
	{
		public Game.BotWalk botwalk { get; private set; }

        public bool hasBotWalk { get { return botwalk != null; } }

        public void setBotWalk(UnityEngine.Vector3 from)
        {
			if (botwalk == null)
				botwalk = pool.botwalk.Get();

			botwalk.from = from;

			react.On(botwalk);
        }

		private void addListener(Listener<Game.BotWalk> obj)
		{
			if (obj == null) return;

			react.Add(obj);

			if (botwalk != null) obj.On(botwalk);
		}

		public void unsetBotWalk(bool needReact = true) 
		{
			remove(botwalk, pool.botwalk);
			botwalk = null;

			if (needReact)
				react.On(botwalk);
		}
