	public partial class EntityListener
	{
		private List<Listener<Game.BotWalk>> onBotWalk = new List<Listener<Game.BotWalk>>();

		public void Add(Listener<Game.BotWalk> obj)
		{
			onBotWalk.Add(obj);
		}

		public void On(Game.BotWalk botwalk)
		{
			foreach(var obj in onBotWalk) obj.On(botwalk);
		}
	}
