    public partial class Entity
	{
		public Game.Hit hit { get; private set; }

        public bool hasHit { get { return hit != null; } }

        public void setHit(System.Single dmg, System.Boolean kill)
        {
			if (hit == null)
				hit = pool.hit.Get();

			hit.dmg = dmg;
			hit.kill = kill;

			react.On(hit);
        }

		private void addListener(Listener<Game.Hit> obj)
		{
			if (obj == null) return;

			react.Add(obj);

			if (hit != null) obj.On(hit);
		}

		public void unsetHit(bool needReact = true) 
		{
			remove(hit, pool.hit);
			hit = null;

			if (needReact)
				react.On(hit);
		}
	}

	public partial class EntityListener
	{
		private List<Listener<Game.Hit>> onHit = new List<Listener<Game.Hit>>();

		public void Add(Listener<Game.Hit> obj)
		{
			onHit.Add(obj);
		}

		public void On(Game.Hit hit)
		{
			foreach(var obj in onHit) obj.On(hit);
		}
	}
